/* draws a filled rectangle */
void draw_filled_rect(int* DDR_addr, int top_left_x, int top_left_y, int length, int width, color c) {
	int i, j;

	for (i = 0; i < length; i++) {
		for (j = 0; j < width; j++) {
			write_rgb(DDR_addr, i + top_left_x, j + top_left_y, c);
		}
	}

}

/* draws a filled circle */
void draw_filled_circle(int* DDR_addr, int cent_x, int cent_y, int radius, color c) {

	int x, y;
	for (x = cent_x - radius; x < cent_x + radius; x++) {
		for (y = cent_y - radius; y < cent_y + radius; y++) {
			if ((cent_x - x) * (cent_x - x) + (cent_y - y) * (cent_y - y)
					<= radius * radius) {
				write_rgb(DDR_addr, x, y, c);
			}
		}
	}
}

/* empties the video buffer. DDR_addr must be aligned to 2M */
void clear_screen(int* DDR_addr) {
	int* DDR_addr_MAX = DDR_addr + 0x200000;
	for (; DDR_addr < DDR_addr_MAX; DDR_addr++) {
//		*DDR_addr = black;
		*DDR_addr = red;
	}
}



