/* sets the video buffer address for the VGA display */
void set_video_mem_addr(volatile int* TFT_addr, int* DDR_addr) {
	TFT_addr[0] = (int) DDR_addr;
}

/* turns off the VGA display */
void disable_vga(volatile int* TFT_addr) {
	TFT_addr[1] = 0x00;
}

/* turns on the VGA display */
void enable_vga(volatile int* TFT_addr) {
	TFT_addr[1] = 0x01;
}


